/*Table structure for table `zbp_post` */

DROP TABLE IF EXISTS `zbp_post`;

CREATE TABLE `zbp_post` (
  `log_ID` int(11) NOT NULL AUTO_INCREMENT,
  `log_CateID` smallint(6) NOT NULL DEFAULT '0',
  `log_AuthorID` int(11) NOT NULL DEFAULT '0',
  `log_Tag` varchar(255) NOT NULL DEFAULT '',
  `log_Status` tinyint(4) NOT NULL DEFAULT '0',
  `log_Type` tinyint(4) NOT NULL DEFAULT '0',
  `log_Alias` varchar(255) NOT NULL DEFAULT '',
  `log_IsTop` tinyint(1) NOT NULL DEFAULT '0',
  `log_IsLock` tinyint(1) NOT NULL DEFAULT '0',
  `log_Title` varchar(255) NOT NULL DEFAULT '',
  `log_Intro` text NOT NULL,
  `log_Content` longtext NOT NULL,
  `log_PostTime` int(11) NOT NULL DEFAULT '0',
  `log_CommNums` int(11) NOT NULL DEFAULT '0',
  `log_ViewNums` int(11) NOT NULL DEFAULT '0',
  `log_Template` varchar(50) NOT NULL DEFAULT '',
  `log_Meta` longtext NOT NULL,
  PRIMARY KEY (`log_ID`),
  KEY `zbp_log_TPISC` (`log_Type`,`log_PostTime`,`log_IsTop`,`log_Status`,`log_CateID`)
) ENGINE=MyISAM AUTO_INCREMENT=45 DEFAULT CHARSET=utf8;

/*Data for the table `zbp_post` */

insert  into `zbp_post`(`log_ID`,`log_CateID`,`log_AuthorID`,`log_Tag`,`log_Status`,`log_Type`,`log_Alias`,`log_IsTop`,`log_IsLock`,`log_Title`,`log_Intro`,`log_Content`,`log_PostTime`,`log_CommNums`,`log_ViewNums`,`log_Template`,`log_Meta`) values (1,1,1,'',0,0,'',0,0,'Welcome To 全栈联盟','<p style=\"white-space: normal;\">欢迎来到全栈联盟，</p><p style=\"white-space: normal;\">本站基于：BAE+MySQL+NodeJs+Express 架构</p>','<p>欢迎来到全栈联盟，</p><p>本站基于：BAE+MySQL+NodeJs+Express 架构</p><p><br/></p>',1459998209,0,339,'',''),(2,0,1,'',0,1,'',0,0,'留言本','','<p>这是一个留言本,是由程序自动生成,您可以编辑修改.</p>',1459998209,0,278,'',''),(3,2,1,'{7}{8}',0,0,'Node连接Mysql数据库',0,0,'Node连接Mysql数据库','<p>进入mysql目录中，查看README文档，这步很重要，不要到处百度Google搜索怎么用，因为由于版本的不一样，也许你得到的答案并不能使你成功连接数据库。毕竟Node发展如此之快。</p>','<h3 style=\"margin: 1.2em 0px 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; font-weight: normal; color: rgb(51, 51, 51); text-rendering: optimizeLegibility; font-size: 18px; line-height: 27px; white-space: normal;\">1.安装Node-mysql</h3><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; color: rgb(51, 51, 51); white-space: normal;\">通过npm安装mysql的软件包，通过它方便快速调用函数连接mysql数据库。进入项目文件夹，执行<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">npm install mysql --save</code>就行了。</p><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; color: rgb(51, 51, 51); white-space: normal;\">安装完，在项目文件夹的<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">node_modules</code>目录下会生成<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">mysql</code>的目录。</p><h3 style=\"margin: 1.2em 0px 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; font-weight: normal; color: rgb(51, 51, 51); text-rendering: optimizeLegibility; font-size: 18px; line-height: 27px; white-space: normal;\">2.查看readme文档</h3><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; color: rgb(51, 51, 51); white-space: normal;\">进入mysql目录中，查看README文档，这步很重要，不要到处百度Google搜索怎么用，因为由于版本的不一样，也许你得到的答案并不能使你成功连接数据库。毕竟Node发展如此之快。</p><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; color: rgb(51, 51, 51); white-space: normal;\">如果你认真读了README文档，接下来的步骤就不用再看了，避免由于版本不一致而误导你。</p><h3 style=\"margin: 1.2em 0px 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; font-weight: normal; color: rgb(51, 51, 51); text-rendering: optimizeLegibility; font-size: 18px; line-height: 27px; white-space: normal;\">3.连接mysql数据库</h3><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; color: rgb(51, 51, 51); white-space: normal;\">进入项目文档，新建TestMysql.js示例，编写如下代码:</p><pre class=\"prettyprint\" style=\"padding: 8.5px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); border-radius: 4px; margin-top: 0px; margin-bottom: 18px; line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;mysql&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;require(&#39;mysql&#39;);\r\n\r\nvar&nbsp;db&nbsp;=&nbsp;mysql.createConnection({\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;host&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&#39;localhost&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;user&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&#39;root&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;password&nbsp;:&nbsp;&#39;root&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;database&nbsp;:&nbsp;&#39;dbname&#39;\r\n&nbsp;&nbsp;});\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;db.connect();\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;connection.query(&#39;SELECT&nbsp;u_name&nbsp;from&nbsp;tab_user&#39;,&nbsp;function(err,&nbsp;data)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(err)&nbsp;throw&nbsp;err;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(&#39;name&nbsp;is:&nbsp;&#39;,&nbsp;data[0].u_name);\r\n&nbsp;&nbsp;});\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;db.end();</pre><ul style=\"padding: 0px; margin-bottom: 9px; margin-left: 25px; color: rgb(51, 51, 51); font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: inherit; font-size: inherit; line-height: inherit;\">连接基本参数</p></li><ul style=\"list-style-type: square;\" class=\" list-paddingleft-2\"><li><p><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">host</code>&nbsp;主机名，localhost代表本地</p></li><li><p><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">user</code>&nbsp;Mysql用户</p></li><li><p><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">password</code>&nbsp;密码</p></li><li><p><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">database</code>&nbsp;连接的数据库</p></li></ul><li><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: inherit; font-size: inherit; line-height: inherit;\"><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">db.connect()</code>连接数据库</p></li><li><p><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">db.query()</code>执行SQL语句</p></li><li><p><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">db.end()</code>关闭连接。</p></li></ul><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; color: rgb(51, 51, 51); white-space: normal;\">然后通过<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">node TestMysql.js</code>执行程序，确保你在执行之前已经启动了<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">Mysql</code>服务。</p><h3 style=\"margin: 1.2em 0px 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; font-weight: normal; color: rgb(51, 51, 51); text-rendering: optimizeLegibility; font-size: 18px; line-height: 27px; white-space: normal;\">5. 增删改查</h3><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; color: rgb(51, 51, 51); white-space: normal;\">使用数据库无外乎<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);\">增删改查</code>，下面示例可能会对你有些帮助。</p><pre class=\"prettyprint\" style=\"padding: 8.5px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); border-radius: 4px; margin-top: 0px; margin-bottom: 18px; line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;mysql&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;require(&#39;mysql&#39;);\r\n\r\nvar&nbsp;db&nbsp;=&nbsp;mysql.createConnection({\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;host&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&#39;localhost&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;user&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&#39;root&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;password&nbsp;:&nbsp;&#39;root&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;database&nbsp;:&nbsp;&#39;dbname&#39;\r\n&nbsp;&nbsp;});\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;db.connect();\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;//&nbsp;增加记录\r\n&nbsp;&nbsp;db.query(&#39;insert&nbsp;into&nbsp;tab_user&nbsp;(u_name&nbsp;,u_phone)&nbsp;values&nbsp;(&quot;totter&quot;&nbsp;,&nbsp;&quot;13800138111&quot;)&#39;);\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;//&nbsp;删除记录\r\n&nbsp;&nbsp;db.query(&#39;delete&nbsp;from&nbsp;tab_user&nbsp;where&nbsp;u_name&nbsp;=&nbsp;&quot;totter&quot;&#39;);\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;//&nbsp;修改记录\r\n&nbsp;&nbsp;db.query(&#39;update&nbsp;tab_user&nbsp;set&nbsp;u_name&nbsp;=&nbsp;&quot;totter123&quot;&nbsp;where&nbsp;u_id&nbsp;=&nbsp;1&#39;);\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;//&nbsp;查询记录\r\n&nbsp;&nbsp;db.query(&quot;select&nbsp;*&nbsp;from&nbsp;tab_user&quot;&nbsp;,&nbsp;function&nbsp;(err,&nbsp;data){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(err){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;throw&nbsp;err;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(data){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(var&nbsp;i&nbsp;=&nbsp;0&nbsp;;&nbsp;i&nbsp;&lt;&nbsp;data.length&nbsp;;&nbsp;i++){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(&quot;%d\\t%s\\t%s&quot;,&nbsp;data[i].u_id,data[i].u_name,data[i].u_phone);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;});\r\n&nbsp;&nbsp;\r\n&nbsp;&nbsp;db.end();</pre><p style=\"margin-top: 1em; margin-bottom: 1em; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; line-height: 23.8px; color: rgb(51, 51, 51); white-space: normal;\">到此，Mysql数据库的初步连接就告一段落了，接下来就可以在Node项目中自行发挥了。</p>',1459998315,1,491,'',''),(11,8,1,'',0,0,'',0,0,'迁移到 Express 4.x','<p>Express 4 不再依赖 Connect，并且从核心中移除了所有内建的中间件，除了 express.static 之外。这意味着 Express 现在是一个不依赖路由和中间件的 Web 框架。这样 Express 的版本化和发布就不再受到中间件的影响。</p>','<h1 style=\"margin: 0px; padding: 0px; font-size: 28px; font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal;\">概览</h1><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">从 Express 3 到Express 4 是一个巨大的变化，这意味着现存的 Express 3 应用在不更新依赖的情况下将不能工作。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;这篇文章涵盖一下内容：</p><ul style=\"margin-left: 30px; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p>Express 4 中的变化</p></li><li><p>一个从 Express 3 迁移到 Express 4 的示例</p></li><li><p>升级到 Express 4 的应用生成器</p></li></ul><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><h1 style=\"margin: 0px; padding: 0px; font-size: 28px; font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal;\">Express 4 中的变化</h1><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">主要的变化如下：</p><ul style=\"margin-left: 30px; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p>Express 的核心和中间件系统：对 Connect 和内建中间件的依赖被移除了。所以你必须自己添加中间件。</p></li><li><p>路由系统</p></li><li><p>其它</p></li></ul><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">参见：</p><ul style=\"margin-left: 30px; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p>4.X 中的新特性</p></li><li><p>从 3.X 迁移到 4.X</p></li></ul><h3 style=\"margin: 0px; padding: 0px; font-size: 16px; font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal;\">Express 的核心和中间件系统</h3><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">Express 4 不再依赖 Connect，并且从核心中移除了所有内建的中间件，除了 express.static 之外。这意味着 Express 现在是一个不依赖路由和中间件的 Web 框架。这样 Express 的版本化和发布就不再受到中间件的影响。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">随着内建的中间件被移除，你必须显式添加所有依赖的中间件来运行你的应用，简单来说需要以下步骤：</p><ol style=\"padding: 0px 0px 0px 40px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p>安装模块：npm install –save 模块名称</p></li><li><p>在你的应用中，使用这个模块： require( 模块名称 )</p></li><li><p>基于模块的文档来使用模块</p></li></ol><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">下表列出了 Express 3 中对应 Express 4 中的模块</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><table class=\"doctable\"><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\" class=\"firstRow\"><th style=\"margin: 0px; padding: 3px; color: rgb(0, 0, 0); border-color: silver; border-collapse: collapse;\">Express 3</th><th style=\"margin: 0px; padding: 3px; color: rgb(0, 0, 0); border-color: silver; border-collapse: collapse;\">Express 4</th></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.bodyParser</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/body-parser\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">body-parser</a>&nbsp;+&nbsp;<a href=\"https://github.com/expressjs/multer\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">multer</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.compress</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/compression\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">compression</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.cookieSession</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/cookie-session\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">cookie-session</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.cookieParser</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/cookie-parser\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">cookie-parser</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.logger</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/morgan\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">morgan</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.session</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/session\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">express-session</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.favicon</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/serve-favicon\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">serve-favicon</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.responseTime</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/response-time\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">response-time</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.errorHandler</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/errorhandler\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">errorhandler</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.methodOverride</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/method-override\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">method-override</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.timeout</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/timeout\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">connect-timeout</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.vhost</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/vhost\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">vhost</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.csrf</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/csurf\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">csurf</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.directory</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/serve-index\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">serve-index</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.static</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/serve-static\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">serve-static</a></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\">express.timeout</td><td style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><a href=\"https://github.com/expressjs/timeout\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(51, 51, 51);\">connect-timeout</a></td></tr></tbody></table><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">多数情况下，你可以使用 Express 4 中对应 Express 3 的模块来替换老的模块，详细的说明见 GitHub 中文档的说明</p><h4 style=\"margin: 0px; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal;\">App.use 接受的参数</h4><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">在 Express 4 中，现在你可以使用带有一个可变参数的路径来加载中间件，并且从路由处理器中读取参数的值，例如：&nbsp;</p><div class=\"cnblogs_code\" style=\"margin: 5px 0px; padding: 5px; border: 1px solid rgb(204, 204, 204); overflow: auto; white-space: normal; font-family: &#39;Courier New&#39; !important; font-size: 12px !important; background-color: rgb(245, 245, 245);\"><pre style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: pre-wrap; word-wrap: break-word; font-family: &#39;Courier New&#39; !important;\">app.use(&#39;/book/:id&#39;,&nbsp;function(req,&nbsp;res,&nbsp;next)&nbsp;{\r\n&nbsp;&nbsp;console.log(&#39;ID:&#39;,&nbsp;req.params.id);\r\n&nbsp;&nbsp;next();\r\n})</pre></div><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><h3 style=\"margin: 0px; padding: 0px; font-size: 16px; font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal;\">路由系统</h3><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">应用隐式加载路由中间件，所以，现在你不必担心 router 路由中间件加载的次序问题。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">定义路由的方式没有发生变化，现在增加了两个新的特性来帮助组织路由系统。</p><ul style=\"margin-left: 30px; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p>新的方法 route，针对一个路由路径创建链式的路由处理器。</p></li><li><p>新的类 express.Router，创建模块化的路由处理器</p></li></ul><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><h4 style=\"margin: 0px; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal;\">app.route 方法</h4><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">新的 app.route 方法对特定的路由路径创建链式的路由处理器。由于可以在一个地方定义路径，这样有助于创建模块话的路由规则，减少重复。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">路由的详细信息，可以参见 Route() 的文档。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">下面的示例演示了路由的链式定义</p><div class=\"cnblogs_code\" style=\"margin: 5px 0px; padding: 5px; border: 1px solid rgb(204, 204, 204); overflow: auto; white-space: normal; font-family: &#39;Courier New&#39; !important; font-size: 12px !important; background-color: rgb(245, 245, 245);\"><div class=\"cnblogs_code_toolbar\" style=\"margin: 5px 0px 0px; padding: 0px;\"><span class=\"cnblogs_code_copy\" style=\"margin: 0px; padding: 0px 5px 0px 0px; line-height: 1.5 !important;\">.</span></div><div class=\"cnblogs_code_toolbar\" style=\"margin: 5px 0px 0px; padding: 0px;\">&nbsp;</div><pre style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: pre-wrap; word-wrap: break-word; font-family: &#39;Courier New&#39; !important;\">app.route(&#39;/book&#39;)\r\n&nbsp;&nbsp;.get(function(req,&nbsp;res)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;res.send(&#39;Get&nbsp;a&nbsp;random&nbsp;book&#39;);\r\n&nbsp;&nbsp;})\r\n&nbsp;&nbsp;.post(function(req,&nbsp;res)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;res.send(&#39;Add&nbsp;a&nbsp;book&#39;);\r\n&nbsp;&nbsp;})\r\n&nbsp;&nbsp;.put(function(req,&nbsp;res)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;res.send(&#39;Update&nbsp;the&nbsp;book&#39;);\r\n&nbsp;&nbsp;})</pre><div class=\"cnblogs_code_toolbar\" style=\"margin: 5px 0px 0px; padding: 0px;\">&nbsp;</div><div class=\"cnblogs_code_toolbar\" style=\"margin: 5px 0px 0px; padding: 0px;\"><span class=\"cnblogs_code_copy\" style=\"margin: 0px; padding: 0px 5px 0px 0px; line-height: 1.5 !important;\">.</span></div></div><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><h4 style=\"margin: 0px; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal;\">express.Router 类</h4><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">另外一个帮助组织路由的特性是新的类 express.Router，可以帮助创建模块话的路由处理器。一个 Router 的实例就是一个完整的中间件和路由系统，由于这个原因，它经常被称为 迷你应用。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">下面演示了创建一个名为 bird.js 的路由文件，内容如下：</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><div class=\"cnblogs_code\" style=\"margin: 5px 0px; padding: 5px; border: 1px solid rgb(204, 204, 204); overflow: auto; white-space: normal; font-family: &#39;Courier New&#39; !important; font-size: 12px !important; background-color: rgb(245, 245, 245);\"><div class=\"cnblogs_code_toolbar\" style=\"margin: 5px 0px 0px; padding: 0px;\"><span class=\"cnblogs_code_copy\" style=\"margin: 0px; padding: 0px 5px 0px 0px; line-height: 1.5 !important;\">.</span></div><div class=\"cnblogs_code_toolbar\" style=\"margin: 5px 0px 0px; padding: 0px;\">&nbsp;</div><pre style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: pre-wrap; word-wrap: break-word; font-family: &#39;Courier New&#39; !important;\">var&nbsp;express&nbsp;=&nbsp;require(&#39;express&#39;);\r\nvar&nbsp;router&nbsp;=&nbsp;express.Router();\r\n\r\n//&nbsp;middleware&nbsp;specific&nbsp;to&nbsp;this&nbsp;router\r\nrouter.use(function&nbsp;timeLog(req,&nbsp;res,&nbsp;next)&nbsp;{\r\n&nbsp;&nbsp;console.log(&#39;Time:&nbsp;&#39;,&nbsp;Date.now());\r\n&nbsp;&nbsp;next();\r\n})\r\n//&nbsp;define&nbsp;the&nbsp;home&nbsp;page&nbsp;route\r\nrouter.get(&#39;/&#39;,&nbsp;function(req,&nbsp;res)&nbsp;{\r\n&nbsp;&nbsp;res.send(&#39;Birds&nbsp;home&nbsp;page&#39;);\r\n})\r\n//&nbsp;define&nbsp;the&nbsp;about&nbsp;route\r\nrouter.get(&#39;/about&#39;,&nbsp;function(req,&nbsp;res)&nbsp;{\r\n&nbsp;&nbsp;res.send(&#39;About&nbsp;birds&#39;);\r\n})\r\n\r\nmodule.exports&nbsp;=&nbsp;router;</pre><div class=\"cnblogs_code_toolbar\" style=\"margin: 5px 0px 0px; padding: 0px;\">&nbsp;</div><div class=\"cnblogs_code_toolbar\" style=\"margin: 5px 0px 0px; padding: 0px;\"><span class=\"cnblogs_code_copy\" style=\"margin: 0px; padding: 0px 5px 0px 0px; line-height: 1.5 !important;\">.</span></div></div><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">然后在应用中加载这个路由模块。</p><div class=\"cnblogs_code\" style=\"margin: 5px 0px; padding: 5px; border: 1px solid rgb(204, 204, 204); overflow: auto; white-space: normal; font-family: &#39;Courier New&#39; !important; font-size: 12px !important; background-color: rgb(245, 245, 245);\"><pre style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: pre-wrap; word-wrap: break-word; font-family: &#39;Courier New&#39; !important;\">var&nbsp;birds&nbsp;=&nbsp;require(&#39;./birds&#39;);\r\n...\r\napp.use(&#39;/birds&#39;,&nbsp;birds);</pre></div><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">这个应用现在可以处理请求 /birds 和 /birds/about，还可以调用 timeLog 中间件。</p><h3 style=\"margin: 0px; padding: 0px; font-size: 16px; font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal;\">其它变化</h3><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">下表列出了其它小的但是重要的修改</p><table cellspacing=\"0\" cellpadding=\"0\"><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\" class=\"firstRow\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">对象</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">说明</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Node</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Express 4 需要 Node 0.10.x 及其以上版本，已经不支持 0.8.x</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">http.createServer</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">不再需要 http 模块，应用使用 app.listen() 启动</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">app.configure()</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">app.configure() 已经被移除，使用 process.env.NODE_ENV 或者 app.get(“env”) 来检测环境、配置应用</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">json.spaces()</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">在 Express 4 中默认禁用了 json spaces</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Req.location()</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">不再能获取相对 url</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Req.params</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">原来是一个数组，现在是对象</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Res.locals</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">原来是函数，现在是对象</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Res.headerSent</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">修改为 res.headersSent</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">App.route</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">现在作为 app.mountpath 存在</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Res.on(“header”)</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">删除</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Res.charset</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">删除</p></td></tr><tr style=\"margin: 0px; padding: 0px;\"><td valign=\"top\" width=\"113\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">Res.setHeader(“Set-Cookie”, val)</p></td><td valign=\"top\" width=\"440\" style=\"margin: 0px; padding: 3px; border-color: silver; border-collapse: collapse;\"><p style=\"margin: 10px auto;\">这个功能现在限制为设置基本的 cookie 值，使用 res.cookie() 的添加功能</p></td></tr></tbody></table><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.3333px; line-height: 24px; white-space: normal;\">&nbsp;</p><p>.</p>',1460964454,0,281,'',''),(4,5,1,'{2}{3}',0,0,'closure',0,0,'闭包的更多作用','<p>过程与数据的结合是形容面向对象中的“对象”时经常使用的表达。对象以方法的形式包含了过程，而闭包则是在过程中以环境的形式包含了数据。通常用面向对象思想能实现的功能，用闭包也能实现。反之亦然。在JavaScript语言的祖先Scheme语言中，甚至都没有提供面向对象的原生设计，但可以使用闭包来实现一个完整的面向对象系统。</p>','<p style=\"text-align: center;\"><img src=\"{#ZC_BLOG_HOST#}zb_users/upload/2016/04/201604091460179519272434.jpg\" alt=\"2.jpg\"/></p><p style=\"white-space: normal;\"><strong>1. 封装变量</strong></p><p style=\"white-space: normal;\">闭包可以帮助把一些不需要暴露在全局的变量封装成“私有变量”。假设有一个计算乘积的简单函数：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;mult&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(&nbsp;var&nbsp;i&nbsp;=&nbsp;0,&nbsp;l&nbsp;=&nbsp;arguments.length;&nbsp;i&nbsp;&lt;&nbsp;l;&nbsp;i++&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a&nbsp;=&nbsp;a&nbsp;*&nbsp;arguments[i];\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;a;};</pre><p style=\"white-space: normal;\"><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">mult</code>函数接受一些<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">number</code>类型的参数，并返回这些参数的乘积。现在我们觉得对于那些相同的参数来说，每次都进行计算是一种浪费，我们可以加入缓存机制来提高这个函数的性能：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;cache&nbsp;=&nbsp;{};var&nbsp;mult&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;args&nbsp;=&nbsp;Array.prototype.join.call(&nbsp;arguments,&nbsp;&#39;,&#39;&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(&nbsp;cache[&nbsp;args&nbsp;]&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;cache[&nbsp;args&nbsp;];\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(&nbsp;var&nbsp;i&nbsp;=&nbsp;0,&nbsp;l&nbsp;=&nbsp;arguments.length;&nbsp;i&nbsp;&lt;&nbsp;l;&nbsp;i++&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a&nbsp;=&nbsp;a&nbsp;*&nbsp;arguments[i];\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;cache[&nbsp;args&nbsp;]&nbsp;=&nbsp;a;};\r\n&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;mult(&nbsp;1,2,3&nbsp;)&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：6\r\n&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;mult(&nbsp;1,2,3&nbsp;)&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：6</pre><p style=\"white-space: normal;\">我们看到<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">cache</code>这个变量仅仅在<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">mult</code>函数中被使用，与其让<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">cache</code>变量跟<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">mult</code>函数一起平行地暴露在全局作用域下，不如把它封闭在<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">mult</code>函数内部，这样可以减少页面中的全局变量，以避免这个变量在其他地方被不小心修改而引发错误。代码如下：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;mult&nbsp;=&nbsp;(function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;cache&nbsp;=&nbsp;{};\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;args&nbsp;=&nbsp;Array.prototype.join.call(&nbsp;arguments,&nbsp;&#39;,&#39;&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(&nbsp;args&nbsp;in&nbsp;cache&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;cache[&nbsp;args&nbsp;];\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(&nbsp;var&nbsp;i&nbsp;=&nbsp;0,&nbsp;l&nbsp;=&nbsp;arguments.length;&nbsp;i&nbsp;&lt;&nbsp;l;&nbsp;i++&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a&nbsp;=&nbsp;a&nbsp;*&nbsp;arguments[i];\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;cache[&nbsp;args&nbsp;]&nbsp;=&nbsp;a;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}})();</pre><p style=\"white-space: normal;\">提炼函数是代码重构中的一种常见技巧。如果在一个大函数中有一些代码块能够独立出来，我们常常把这些代码块封装在独立的小函数里面。独立出来的小函数有助于代码复用，如果这些小函数有一个良好的命名，它们本身也起到了注释的作用。如果这些小函数不需要在程序的其他地方使用，最好是把它们用闭包封闭起来。代码如下：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;mult&nbsp;=&nbsp;(function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;cache&nbsp;=&nbsp;{};\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;calculate&nbsp;=&nbsp;function(){&nbsp;&nbsp;&nbsp;//&nbsp;封闭calculate函数\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(&nbsp;var&nbsp;i&nbsp;=&nbsp;0,&nbsp;l&nbsp;=&nbsp;arguments.length;&nbsp;i&nbsp;&lt;&nbsp;l;&nbsp;i++&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a&nbsp;=&nbsp;a&nbsp;*&nbsp;arguments[i];\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;a;\r\n&nbsp;&nbsp;&nbsp;&nbsp;};\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;args&nbsp;=&nbsp;Array.prototype.join.call(&nbsp;arguments,&nbsp;&#39;,&#39;&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(&nbsp;args&nbsp;in&nbsp;cache&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;cache[&nbsp;args&nbsp;];\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;cache[&nbsp;args&nbsp;]&nbsp;=&nbsp;calculate.apply(&nbsp;null,&nbsp;arguments&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}})();</pre><p style=\"white-space: normal;\"><strong>2. 延续局部变量的寿命</strong></p><p style=\"white-space: normal;\"><code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">img</code>对象经常用于进行数据上报，如下所示：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;report&nbsp;=&nbsp;function(&nbsp;src&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;img&nbsp;=&nbsp;new&nbsp;Image();\r\n&nbsp;&nbsp;&nbsp;&nbsp;img.src&nbsp;=&nbsp;src;};report(&nbsp;&#39;http://xxx.com/getUserInfo&#39;&nbsp;);</pre><p style=\"white-space: normal;\">但是通过查询后台的记录我们得知，因为一些低版本浏览器的实现存在bug，在这些浏览器下使用<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">report</code>函数进行数据上报会丢失30%左右的数据，也就是说，<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">report</code>函数并不是每一次都成功发起了HTTP请求。丢失数据的原因是<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">img</code>是<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">report</code>函数中的局部变量，当<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">report</code>函数的调用结束后，<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">img</code>局部变量随即被销毁，而此时或许还没来得及发出HTTP请求，所以此次请求就会丢失掉。</p><p style=\"white-space: normal;\">现在我们把<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">img</code>变量用闭包封闭起来，便能解决请求丢失的问题：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">&nbsp;var&nbsp;report&nbsp;=&nbsp;(function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;imgs&nbsp;=&nbsp;[];\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;function(&nbsp;src&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;img&nbsp;=&nbsp;new&nbsp;Image();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;imgs.push(&nbsp;img&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;img.src&nbsp;=&nbsp;src;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}})();</pre><h3 style=\"white-space: normal; margin: 18px 0px 9px; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; font-weight: normal; color: rgb(102, 102, 102); text-rendering: optimizeLegibility; font-size: 18px; line-height: 27px; background-color: rgb(255, 255, 255);\"><strong>3.1.4　闭包和面向对象设计</strong></h3><p style=\"white-space: normal;\">过程与数据的结合是形容面向对象中的“对象”时经常使用的表达。对象以方法的形式包含了过程，而闭包则是在过程中以环境的形式包含了数据。通常用面向对象思想能实现的功能，用闭包也能实现。反之亦然。在JavaScript语言的祖先Scheme语言中，甚至都没有提供面向对象的原生设计，但可以使用闭包来实现一个完整的面向对象系统。</p><p style=\"white-space: normal;\">下面来看看这段跟闭包相关的代码：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;extent&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;value&nbsp;=&nbsp;0;\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;call:&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value++;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(&nbsp;value&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;}};var&nbsp;extent&nbsp;=&nbsp;extent();\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：1\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：2\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：3</pre><p style=\"white-space: normal;\">如果换成面向对象的写法，就是：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;extent&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;value:&nbsp;0,\r\n&nbsp;&nbsp;&nbsp;&nbsp;call:&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.value++;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(&nbsp;this.value&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}};\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：1\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：2\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：3</pre><p style=\"white-space: normal;\">或者：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;Extent&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;this.value&nbsp;=&nbsp;0;};Extent.prototype.call&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;this.value++;\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(&nbsp;this.value&nbsp;);};\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;extent&nbsp;=&nbsp;new&nbsp;Extent();\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();\r\n&nbsp;&nbsp;&nbsp;&nbsp;extent.call();</pre><p><br/></p>',1460081276,0,297,'',''),(5,5,1,'{2}{3}',0,0,'closure',0,0,'闭包和高阶函数','<p>对于JavaScript程序员来说，<strong style=\"white-space: normal;\">闭包</strong>（closure）是一个难懂又必须征服的概念。闭包的形成与变量的作用域以及变量的生存周期密切相关。下面我们先简单了解这两个知识点。</p>','<p style=\"text-align: center;\"><img src=\"{#ZC_BLOG_HOST#}zb_users/upload/2016/04/201604091460182811674308.jpg\" alt=\"4.jpg\"/></p><p style=\"white-space: normal;\">对于JavaScript程序员来说，<strong>闭包</strong>（closure）是一个难懂又必须征服的概念。闭包的形成与变量的作用域以及变量的生存周期密切相关。下面我们先简单了解这两个知识点。</p><h3 style=\"white-space: normal; margin: 18px 0px 9px; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; font-weight: normal; color: rgb(102, 102, 102); text-rendering: optimizeLegibility; font-size: 18px; line-height: 27px; background-color: rgb(255, 255, 255);\"><strong>变量的作用域</strong></h3><p style=\"white-space: normal;\">变量的作用域，就是指变量的有效范围。我们最常谈到的是在函数中声明的变量作用域。</p><p style=\"white-space: normal;\">当在函数中声明一个变量的时候，如果该变量前面没有带上关键字<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">var</code>，这个变量就会成为全局变量，这当然是一种容易造成命名冲突的做法。</p><p style=\"white-space: normal;\">另外一种情况是用<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">var</code>关键字在函数中声明变量，这时候的变量即是局部变量，只有在该函数内部才能访问到这个变量，在函数外面是访问不到的。代码如下：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;func&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;a&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出:&nbsp;1};func();alert&nbsp;(&nbsp;a&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：Uncaught&nbsp;ReferenceError:&nbsp;a&nbsp;is&nbsp;not&nbsp;defined</pre><p style=\"white-space: normal;\">在JavaScript中，函数可以用来创造函数作用域。此时的函数像一层半透明的玻璃，在函数里面可以看到外面的变量，而在函数外面则无法看到函数里面的变量。这是因为当在函数中搜索一个变量的时候，如果该函数内并没有声明这个变量，那么此次搜索的过程会随着代码执行环境创建的作用域链往外层逐层搜索，一直搜索到全局对象为止。变量的搜索是从内到外而非从外到内的。</p><p style=\"white-space: normal;\">下面这段包含了嵌套函数的代码，也许能帮助我们加深对变量搜索过程的理解：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;a&nbsp;=&nbsp;1;var&nbsp;func1&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;b&nbsp;=&nbsp;2;\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;func2&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;c&nbsp;=&nbsp;3;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;b&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：2\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;a&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：1\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;func2();\r\n&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;c&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：Uncaught&nbsp;ReferenceError:&nbsp;c&nbsp;is&nbsp;not&nbsp;defined};func1();</pre><h3 style=\"white-space: normal; margin: 18px 0px 9px; font-family: MicrosoftYaHei, 微软雅黑, Arial, Tahoma, Helvetica, Georgia, sans-serif, STXihei, 华文黑体, Hei, &#39;Hiragino Kaku Gothic Pro&#39;, SimSun; font-weight: normal; color: rgb(102, 102, 102); text-rendering: optimizeLegibility; font-size: 18px; line-height: 27px; background-color: rgb(255, 255, 255);\"><strong>变量的生存周期</strong></h3><p style=\"white-space: normal;\">除了变量的作用域之外，另外一个跟闭包有关的概念是变量的生存周期。</p><p style=\"white-space: normal;\">对于全局变量来说，全局变量的生存周期当然是永久的，除非我们主动销毁这个全局变量。</p><p style=\"white-space: normal;\">而对于在函数内用<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">var</code>关键字声明的局部变量来说，当退出函数时，这些局部变量即失去了它们的价值，它们都会随着函数调用的结束而被销毁：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;func&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;退出函数后局部变量a将被销毁\r\n&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;a&nbsp;);};func();</pre><p style=\"white-space: normal;\">现在来看看下面这段代码：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;func&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a++;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;a&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}};var&nbsp;f&nbsp;=&nbsp;func();f();&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：2f();&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：3f();&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：4f();&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：5</pre><p style=\"white-space: normal;\">跟我们之前的推论相反，当退出函数后，局部变量<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">a</code>并没有消失，而是似乎一直在某个地方存活着。这是因为当执行<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">var f = func();</code>时，<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">f</code>返回了一个匿名函数的引用，它可以访问到<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">func()</code>被调用时产生的环境，而局部变量<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">a</code>一直处在这个环境里。既然局部变量所在的环境还能被外界访问，这个局部变量就有了不被销毁的理由。在这里产生了一个闭包结构，局部变量的生命看起来被延续了。</p><p style=\"white-space: normal;\">利用闭包我们可以完成许多奇妙的工作，下面介绍一个闭包的经典应用。假设页面上有5个<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">div</code>节点，我们通过循环来给每个<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">div</code>绑定<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">onclick</code>事件，按照索引顺序，点击第1个<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">div</code>时弹出0，点击第2个<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">div</code>时弹出1，以此类推。代码如下：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">&lt;html&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;body&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div&gt;1&lt;/div&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div&gt;2&lt;/div&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div&gt;3&lt;/div&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div&gt;4&lt;/div&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div&gt;5&lt;/div&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&gt;\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;nodes&nbsp;=&nbsp;document.getElementsByTagName(&nbsp;&#39;div&#39;&nbsp;);\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(&nbsp;var&nbsp;i&nbsp;=&nbsp;0,&nbsp;len&nbsp;=&nbsp;nodes.length;&nbsp;i&nbsp;&lt;&nbsp;len;&nbsp;i++&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;nodes[&nbsp;i&nbsp;].onclick&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert&nbsp;(&nbsp;i&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;};\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/body&gt;&lt;/html&gt;</pre><p style=\"white-space: normal;\">测试这段代码就会发现，无论点击哪个<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">div</code>，最后弹出的结果都是5。这是因为<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">div</code>节点的<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">onclick</code>事件是被异步触发的，当事件被触发的时候，<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">for</code>循环早已结束，此时变量<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">i</code>的值已经是5，所以在<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">div</code>的<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">onclick</code>事件函数中顺着作用域链从内到外查找变量<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">i</code>时，查找到的值总是5。</p><p style=\"white-space: normal;\">解决方法是在闭包的帮助下，把每次循环的<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">i</code>值都封闭起来。当在事件函数中顺着作用域链中从内到外查找变量<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">i</code>时，会先找到被封闭在闭包环境中的<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">i</code>，如果有5个<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">div</code>，这里的<code style=\"padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; border: 1px solid rgb(225, 225, 232);\">i</code>就分别是0,1,2,3,4：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">for&nbsp;(&nbsp;var&nbsp;i&nbsp;=&nbsp;0,&nbsp;len&nbsp;=&nbsp;nodes.length;&nbsp;i&nbsp;&lt;&nbsp;len;&nbsp;i++&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;(function(&nbsp;i&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;nodes[&nbsp;i&nbsp;].onclick&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(i);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;})(&nbsp;i&nbsp;)};</pre><p style=\"white-space: normal;\">根据同样的道理，我们还可以编写如下一段代码：</p><pre class=\"代码无行号 prettyprint\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 8.5px; border-radius: 4px; font-family: Menlo, Monaco, Consolas, &#39;Courier New&#39;, monospace; font-size: 12px; color: rgb(51, 51, 51); line-height: 18px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);\">var&nbsp;Type&nbsp;=&nbsp;{};for&nbsp;(&nbsp;var&nbsp;i&nbsp;=&nbsp;0,&nbsp;type;&nbsp;type&nbsp;=&nbsp;[&nbsp;&#39;String&#39;,&nbsp;&#39;Array&#39;,&nbsp;&#39;Number&#39;&nbsp;][&nbsp;i++&nbsp;];&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;(function(&nbsp;type&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Type[&nbsp;&#39;is&#39;&nbsp;+&nbsp;type&nbsp;]&nbsp;=&nbsp;function(&nbsp;obj&nbsp;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Object.prototype.toString.call(&nbsp;obj&nbsp;)&nbsp;===&nbsp;&#39;[object&nbsp;&#39;+&nbsp;type&nbsp;+&#39;]&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;})(&nbsp;type&nbsp;)};Type.isArray(&nbsp;[]&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：trueType.isString(&nbsp;&quot;str&quot;&nbsp;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;输出：true</pre><p><br/></p>',1460081444,0,323,'',''),(6,7,1,'',0,0,'',0,0,'Css3的Media Query方法总结','<p>Media Query可以结合多个媒体查询，换句话说，一个Media Query可以包含0到多个表达式，表达式又可以包含0到多个关键字，以及一种Media Type。正如上面的其表示的是当屏幕在600px-900px之间时采用style.css样式来渲染web页面。</p>','<p style=\"text-align: center;\"><img src=\"{#ZC_BLOG_HOST#}zb_users/upload/2016/04/201604091460182841108181.jpg\" alt=\"5.jpg\"/></p><h2 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 18px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">一、Css3的Media Queries 翻译成中文是“媒体查询”，有如下几种引入方式：</h2><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">1、直接head中引用，其实media在css2中已经存在，不过，他的主要作用您没有关注，兼容所有媒体等。你肯定见到过如下的写法：</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&nbsp;&lt;link&nbsp;href=&quot;css/style.css&quot;&nbsp;rel=&quot;stylesheet&quot;&nbsp;type=&quot;text/css&quot;&nbsp;media=&quot;all&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">现在，我们为了兼容屏幕的大小，可以这么写：</p><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;screen&nbsp;and&nbsp;(max-width:&nbsp;600px)&quot;&nbsp;href=&quot;smallscreen.css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">在屏幕最大是600px的时候加载“smallscreen.css”</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">2、@import 方式引用，这种方式的引用，要在style中，写法如下：</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&lt;style&nbsp;type=&quot;text/css&quot;&nbsp;media=&quot;screen&quot;&gt;&nbsp;或者写成&lt;style&nbsp;type=&quot;text/css&quot;&nbsp;media=&quot;screen&nbsp;and&nbsp;(max-width:&nbsp;600px)&quot;&gt;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;@import&nbsp;url(&quot;css/style.css&quot;);\r\n&nbsp;&nbsp;&lt;/style&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">也就是在特定屏幕下加载style.css</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">3、我最常用的是第三种方法，也就是下面的这种方法：</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&nbsp;&nbsp;@media&nbsp;screen&nbsp;and&nbsp;(max-width:&nbsp;600px)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;选择器&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;属性：属性值；\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;}</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">直接在样式中写@media屏幕控制。</p><h2 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 18px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">二、Media Queries的具体使用方式</h2><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">1、最大宽度Max Width</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&nbsp;&nbsp;&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;screen&nbsp;and&nbsp;(max-width:600px)&quot;&nbsp;href=&quot;small.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">上面表示的是：当屏幕小于或等于600px时，将采用small.css样式来渲染Web页面。</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">2、最小宽度Min Width</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&nbsp;&nbsp;&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;screen&nbsp;and&nbsp;(min-width:900px)&quot;&nbsp;href=&quot;big.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">上面表示的是：当屏幕大于或等于900px时，将采用big.css样式来渲染Web页面。</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">3、多个Media Queries使用</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&nbsp;&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;screen&nbsp;and&nbsp;(min-width:600px)&nbsp;and&nbsp;(max-width:900px)&quot;&nbsp;href=&quot;style.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">Media Query可以结合多个媒体查询，换句话说，一个Media Query可以包含0到多个表达式，表达式又可以包含0到多个关键字，以及一种Media Type。正如上面的其表示的是当屏幕在600px-900px之间时采用style.css样式来渲染web页面。</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">4、设备屏幕的输出宽度Device Width</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;screen&nbsp;and&nbsp;(max-device-width:&nbsp;480px)&quot;&nbsp;href=&quot;iphone.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">上面的代码指的是iphone.css样式适用于最大设备宽度为480px，比如说iPhone上的显示，这里的max-device-width所指的是设备的实际分辨率，也就是指可视面积分辨率</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">5、iPhone4</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;only&nbsp;screen&nbsp;and&nbsp;(-webkit-min-device-pixel-ratio:&nbsp;2)&quot;&nbsp;type=&quot;text/css&quot;&nbsp;href=&quot;iphone4.css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">上面的样式是专门针对iPhone4的移动设备写的。</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">6、iPad</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&nbsp;&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;all&nbsp;and&nbsp;(orientation:portrait)&quot;&nbsp;href=&quot;portrait.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;&nbsp;\r\n&nbsp;&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;all&nbsp;and&nbsp;(orientation:landscape)&quot;&nbsp;href=&quot;landscape.css&quot;&nbsp;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">在大数情况下，移动设备iPad上的Safari和在iPhone上的是相同的，只是他们不同之处是iPad声明了不同的方向，比如说上面的例子，在纵向(portrait)时采用portrait.css来渲染页面；在横向（landscape）时采用landscape.css来渲染页面。</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">7、android</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&nbsp;/*240px的宽度*/\r\n&nbsp;&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;only&nbsp;screen&nbsp;and&nbsp;(max-device-width:240px)&quot;&nbsp;href=&quot;android240.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;\r\n&nbsp;&nbsp;/*360px的宽度*/\r\n&nbsp;&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;only&nbsp;screen&nbsp;and&nbsp;(min-device-width:241px)&nbsp;and&nbsp;(max-device-width:360px)&quot;&nbsp;href=&quot;android360.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;\r\n&nbsp;&nbsp;/*480px的宽度*/\r\n&nbsp;&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;only&nbsp;screen&nbsp;and&nbsp;(min-device-width:361px)&nbsp;and&nbsp;(max-device-width:480px)&quot;&nbsp;href=&quot;android480.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">我们可以使用media query为android手机在不同分辨率提供特定样式，这样就可以解决屏幕分辨率的不同给android手机的页面重构问题。</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">8、not关键字</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&nbsp;&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;not&nbsp;print&nbsp;and&nbsp;(max-width:&nbsp;1200px)&quot;&nbsp;href=&quot;print.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">not关键字是用来排除某种制定的媒体类型，换句话来说就是用于排除符合表达式的设备。</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">9、only关键字</h3><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;only&nbsp;screen&nbsp;and&nbsp;(max-device-width:240px)&quot;&nbsp;href=&quot;android240.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">only用来定某种特定的媒体类型，可以用来排除不支持媒体查询的浏览器。其实only很多时候是用来对那些不支持Media Query但却支持Media Type的设备隐藏样式表的。其主要有：支持媒体特性（Media Queries）的设备，正常调用样式，此时就当only不存在；对于不支持媒体特性(Media Queries)但又支持媒体类型(Media Type)的设备，这样就会不读了样式，因为其先读only而不是screen；另外不支持Media Qqueries的浏览器，不论是否支持only，样式都不会被采用。</p><h3 style=\"white-space: normal; margin: 0px 0px 10px; padding: 4px 0px; font-size: 15px; border-bottom-width: 1px; border-bottom-style: dashed; border-bottom-color: rgb(204, 204, 204); font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif;\">10、其他</h3><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">在Media Query中如果没有明确指定Media Type，那么其默认为all，如：</p><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;media=&quot;(min-width:&nbsp;701px)&nbsp;and&nbsp;(max-width:&nbsp;900px)&quot;&nbsp;href=&quot;medium.css&quot;&nbsp;type=&quot;text/css&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">另外还有使用逗号（，）被用来表示并列或者表示或，如下</p><pre class=\"prettyprint prettyprinted\" style=\"margin-top: 0px; margin-bottom: 18px; padding: 10px 10px 7px;font-size: 12px; font-family: Menlo, Monaco, Consolas, &#39;Lucida Console&#39;, &#39;Courier New&#39;, &#39;Microsoft Yahei&#39;, monospace; white-space: pre-wrap; line-height: 1.5em; border-style: solid; border-width: 1px 1px 1px 2px; border-color: rgb(221, 221, 221) rgb(221, 221, 221) rgb(221, 221, 221) rgb(27, 88, 184);\">&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;type=&quot;text/css&quot;&nbsp;href=&quot;style.css&quot;&nbsp;media=&quot;handheld&nbsp;and&nbsp;(max-width:480px),&nbsp;screen&nbsp;and&nbsp;(min-width:960px)&quot;&nbsp;/&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 1em; white-space: normal; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, &#39;Trebuchet MS&#39;, Georgia, &#39;Times New Roman&#39;, Times, sans-serif; line-height: 26.6px;\">上面代码中style.css样式被用在宽度小于或等于480px的手持设备上，或者被用于屏幕宽度大于或等于960px的设备上。</p><p><br/></p>',1460081538,0,523,'',''),(10,5,1,'',0,0,'',0,0,'JavaScript 资深全栈 WEB工程师 进阶课程','<p>node.js 这两年的火爆程度着实令人咋舌！在众多开发者强烈追捧的气氛下，纵然你不打算尝试后端开发，也要作为一项技术储备的心态而去学习它!</p>','<p>　　第一章：JS入门 - 原理分析、语法规则、自定义属性操作、if、this、for</p><p>　　</p><p>　　第二章：ECMAScript 5.1、数据类型、函数传参、作用域、运算符、流程控制</p><p>　　</p><p>　　第三章：定时器和日期对象、setInterval、setTimeout、MTween、new Date</p><p>　　</p><p>　　第四章：字符串和数组、JSON、for in、Math对象</p><p>　　</p><p>　　第五章：DOM - DOM基础、DOM节点、元素的操作、表格表单操作</p><p>　　</p><p>　　第六章：BOM - BOM基础、常用方法、常用属性</p><p>　　</p><p>　　第七章：PC/移动端事件基础 - Event对象、事件流、事件默认行为、mTweenPlus实现</p><p>　　</p><p>　　第八章：事件应用 - 简易拖拽、碰撞检测、自定义滚动条</p><p>　　</p><p>　　第九章：正则表达式、new RegExp、子项、转义字符、量词</p><p>　　</p><p>　　第十章：面向对象编程基础、构造函数、prototype原型、原型与原型链</p><p>　　</p><p>　　第十一章：面向对象深入、包装对象、toString使用、继承</p><p>　　</p><p>　　第十二章：面向对象-组件开发、组件配置，extend实现、组件事件</p><p>　　</p><p>　　第十三章：jQuery3介绍-整体架构,css()、html()实现、sizzle.js、Event、动画animate</p><p>　　</p><p>　　第十四章：Ajax介绍及工作原理分析、XMLHttpRequest、onload、onprogress、JSONP原理</p><p>　　</p><p>　　第十五章：JQuery插件、表单验证插件、$().extend()，自定义事件</p><p>　　</p><p>　　第十六章：JavaScript兼容性、forEach、querySelector、attachEvent、stopPropagation</p><p>　　</p><p>　　第十七章：ECMAScript 6与编译工具</p><p>　　</p><p>　　第十八章：miaov.touch.js、touch事件、swipe事件、gesture多指操作、MScroll移动端滑屏框架、体感操作、摇一摇</p><p>　　</p><p>　　第十九章：nodeJS介绍及使用、npm、 node模块、express、 jade模板引擎、 mongodb、tcp协议、udp协议、http协议、webSocket</p><p>　　</p><p>　　第二十章：Git 及 GitHub 使用</p><p>　　</p><p>　　第二十一章：react介绍和使用、JSX介绍、ReactDOM、props、state、组件生命周期</p>',1460963594,0,202,'',''),(7,5,1,'{4}{5}',0,0,'面向对象',0,0,'深入理解JS面向对象','<p style=\"white-space: normal;\">此文章略长，代码略多，专业术语略多，略恶!所以坚持……</p><p style=\"white-space: normal;\">javascript没有真正意义上的类，不过咱可以模拟类!</p><p style=\"white-space: normal;\">每一个函数都有一个prototype的对象属性，每次new函数，出来的对象(实例)，就可以使用原型身上的方法及其属性</p>','<p style=\"text-align: center;\"><img src=\"{#ZC_BLOG_HOST#}zb_users/upload/2016/04/201604091460184378125170.jpg\" alt=\"6.jpg\"/></p><p style=\"white-space: normal;\">　　此文章略长，代码略多，专业术语略多，略恶!所以坚持……</p><p style=\"white-space: normal;\">　　javascript没有真正意义上的类，不过咱可以模拟类!</p><p style=\"white-space: normal;\">　　每一个函数都有一个prototype的对象属性，每次new函数，出来的对象(实例)，就可以使用原型身上的方法及其属性：</p><pre class=\"brush:js;toolbar:false\">	function&nbsp;foo(){\r\n		//叫类，也叫构造函数\r\n	}\r\n	foo.prototype={&nbsp;//原型\r\n		aa:&#39;aa&#39;,\r\n		bb:function(){\r\n			//原型方法，所有实例都可以使用\r\n		}&nbsp;\r\n	};\r\n	\r\n	var&nbsp;a=new&nbsp;foo;\r\n	var&nbsp;b=new&nbsp;foo;\r\n	\r\n	console.log(a.aa==b.aa);&nbsp;//true\r\n	console.log(b.bb==b.bb);&nbsp;//true</pre><p style=\"white-space: normal;\">　　特权方法：直接在构造函数内指定的方法就是特权方法。</p><p style=\"white-space: normal;\">　　私有属性：外界没法直接访问</p><pre class=\"brush:js;toolbar:false\">	function&nbsp;foo(){\r\n		var&nbsp;num=12;&nbsp;//私有属性，外界访问不到\r\n		this.aa=&#39;aa&#39;;&nbsp;//特权属性\r\n		this.bb=function(){&nbsp;//&nbsp;特权方法\r\n			return&nbsp;num;\r\n		};\r\n		this.json={};\r\n	}\r\n	\r\n	foo.prototype={//原型\r\n		aa:&#39;bb&#39;,\r\n		bb:function(){}\r\n	};\r\n	\r\n	var&nbsp;a=new&nbsp;foo;\r\n	var&nbsp;b=new&nbsp;foo;\r\n	\r\n	console.log(a.aa==b.aa);&nbsp;//true,&nbsp;基本类型的字符串,肯定相等\r\n	console.log(a.json==b.json);&nbsp;//false&nbsp;引用类型，每次都进入函数体，重新创建，所以不一样\r\n	console.log(a.bb==b.bb);&nbsp;//false&nbsp;访问的是特权方法\r\n	\r\n	delete&nbsp;a.bb;&nbsp;//删除&nbsp;特权方法，那只剩原型的方法了\r\n	console.log(a.bb==foo.prototype.bb);&nbsp;//true</pre><p style=\"white-space: normal;\">　　原型方法和特权方法都属于实例(对象)方法</p><p style=\"white-space: normal;\">　　当然还有个概念叫做类方法(类属性)：就是直接往构造函数(类)身上添加的方法(属性)：</p><pre class=\"brush:js;toolbar:false\">	function&nbsp;foo(){\r\n		var&nbsp;num=12;&nbsp;//私有属性，外界访问不到\r\n		this.aa=&#39;aa&#39;;&nbsp;//特权属性\r\n		this.bb=function(){&nbsp;//&nbsp;特权方法\r\n			return&nbsp;num;\r\n		};\r\n		this.json={};\r\n	}\r\n	\r\n	foo.prototype={//原型\r\n		aa:&#39;bb&#39;,\r\n		bb:function(){}\r\n	};\r\n	\r\n	foo.bb2=function(){\r\n	\r\n	};\r\n	var&nbsp;c=new&nbsp;foo;\r\n	console.log(c.bb2);&nbsp;//undefine</pre><p style=\"white-space: normal;\">　　继承的实现：</p><p style=\"white-space: normal;\">　　一、直接置换</p><pre class=\"brush:js;toolbar:false\">	function&nbsp;foo(){\r\n	\r\n	}\r\n	foo.prototype.a={\r\n		show:11\r\n	};\r\n	function&nbsp;bar(){\r\n	\r\n	}\r\n	bar.prototype.a={\r\n		show:11\r\n	};\r\n	\r\n	console.log(foo.prototype.a);//Object&nbsp;{show:&nbsp;11}\r\n	console.log(bar.prototype.a);//Object&nbsp;{show:&nbsp;11}\r\n	console.log(bar.prototype==foo.prototype);//false\r\n	\r\n	bar.prototype=foo.prototype;\r\n	console.log(bar.prototype==foo.prototype);//true\r\n	//对象之间是引用，所以，修改bar也会影响foo</pre><p style=\"white-space: normal;\">　　二、通过for in 把父级原型逐个付给子类原型 俗称：拷贝继承,好处简单，暴力，坏处—无法通过instanceof验证</p><pre class=\"brush:js;toolbar:false\">	function&nbsp;extend(son,parent){\r\n		for(var&nbsp;name&nbsp;in&nbsp;parent.prototype){\r\n			son.prototype[name]=parent.prototype[name];\r\n		}\r\n		return&nbsp;son;\r\n	}\r\n	\r\n	function&nbsp;foo(){	}&nbsp;//函数foo\r\n	foo.prototype={	aa:11};\r\n	\r\n	function&nbsp;bar(){	}&nbsp;//函数bar\r\n	bar.prototype={	bb:22};\r\n	\r\n	var&nbsp;bar=extend(bar,foo);//实现继承（把函数foo的aa集成过来）\r\n		bar.prototype.cc=33;//继承后赋值（在上原型赋值不受影响）\r\n	&nbsp;\r\n	var&nbsp;a=new&nbsp;foo;\r\n	var&nbsp;b=new&nbsp;bar;\r\n	\r\n	console.log(a.aa);&nbsp;//11&nbsp;函数foo原型上的aa\r\n	console.log(a.bb);&nbsp;//undefined&nbsp;函数foo压根就没有过bb\r\n	console.log(a.cc);&nbsp;//undefined&nbsp;函数foo压根就没有过cc\r\n	console.log(b.aa);&nbsp;//11&nbsp;证明继承过来了\r\n	console.log(b.bb);&nbsp;//22&nbsp;继承之前bb的值\r\n	console.log(b.cc);&nbsp;//33&nbsp;继承之后cc的值</pre><p style=\"white-space: normal;\">　　三、将父类的原型赋给一个函数，函数的实例作为子类的原型， 俗称:原型继承</p><pre class=\"brush:js;toolbar:false\">	//1\r\n	function&nbsp;foo(){	}\r\n	foo.prototype={\r\n		aa:function(){\r\n			alert(1);\r\n		}\r\n	};\r\n	&nbsp;//2\r\n	function&nbsp;bridge(){};\r\n	bridge.prototype=foo.prototype;&nbsp;//父类的原型给中介函数的原型\r\n	&nbsp;\r\n	//3\r\n	function&nbsp;bar(){	};\r\n	bar.prototype=new&nbsp;bridge();\r\n	\r\n	//实例化...\r\n	var&nbsp;a=new&nbsp;foo;\r\n	var&nbsp;b=new&nbsp;bar;\r\n	\r\n	console.log(foo.prototype==bar.prototype);&nbsp;//false&nbsp;成功把它俩分开	&nbsp;\r\n	console.log(a.aa==b.aa);&nbsp;//true&nbsp;继承下来方法了\r\n	&nbsp;\r\n	foo.prototype.show=function(){&nbsp;//给父类添加方法\r\n		alert(2);\r\n	};	&nbsp;\r\n	b.show();&nbsp;//子类可以使用，因为继承了\r\n	&nbsp;\r\n	bar.prototype.fnb=function(){&nbsp;//给子类添加方法\r\n		alert(3);\r\n	};	\r\n	console.log(a.fnb==b.fnb);&nbsp;//false&nbsp;父级对象用不到，是ok的\r\n	\r\n	//堪称完美的继承\r\n	console.log(a&nbsp;instanceof&nbsp;foo);&nbsp;//true\r\n	console.log(b&nbsp;instanceof&nbsp;bar);&nbsp;//true\r\n	console.log(b&nbsp;instanceof&nbsp;foo);&nbsp;//true</pre><p style=\"white-space: normal;\">　　四、当然ES5，自带了一个继承的东西，就是 Object.create();</p><pre class=\"brush:js;toolbar:false\">	function&nbsp;foo(){	}\r\n	foo.prototype={\r\n		aa:function(){\r\n			alert(11);\r\n		}\r\n	};\r\n	&nbsp;\r\n	function&nbsp;bar(){	};	&nbsp;\r\n	bar.prototype=Object.create(new&nbsp;foo);//继承了foo的aa\r\n	bar.prototype.bb=22;\r\n	&nbsp;\r\n	var&nbsp;a=new&nbsp;foo;\r\n	var&nbsp;b=new&nbsp;bar;\r\n		b.aa();//继承过来的\r\n\r\n	alert(a.bb);&nbsp;//undefined\r\n	alert(b.bb);&nbsp;//22</pre><p style=\"white-space: normal;\">　　当然Object.create()不兼容IE8以下，不过可以封装函数实现兼容：</p><pre class=\"brush:js;toolbar:false\">	\r\n	Object.create=Object.create&nbsp;||&nbsp;function(obj){\r\n		function&nbsp;Fn(){};\r\n		Fn.prototype=obj;\r\n		return&nbsp;new&nbsp;Fn();\r\n	};</pre><p style=\"white-space: normal;\">　　以上各种方法都是继承了父类的方法，父类的类成员与特权成员都没有继承，所以：</p><p style=\"white-space: normal;\">　　最后封装函数：</p><pre class=\"brush:js;toolbar:false\">//封装1\r\nObject.create=Object.create&nbsp;||&nbsp;function(obj){\r\n	function&nbsp;Fn(){};\r\n	Fn.prototype=obj;\r\n	return&nbsp;new&nbsp;Fn();\r\n};\r\n//封装2\r\nfunction&nbsp;extend(des,source){\r\n	for(var&nbsp;name&nbsp;in&nbsp;source)\r\n	des[name]=source[name];\r\n	return&nbsp;des;\r\n}\r\n//封装3\r\nfunction&nbsp;inherit(init,Parent,proto){\r\n	function&nbsp;Son(){\r\n		Parent.apply(this,arguments);&nbsp;//继承父类的属性\r\n		init.apply(this.arguments);&nbsp;//执行自己的构造函数\r\n	}\r\n	Son.prototype=Object.create(Parent.prototype,{});&nbsp;//\r\n	Son.prototype.toString=Parent.prototype.toString;&nbsp;//处理&nbsp;IE&nbsp;bug\r\n	Son.prototype.valueOf=Parent.prototype.valueOf;&nbsp;//&nbsp;处理&nbsp;IE&nbsp;bug\r\n	Son.prototype.constructor=Son;&nbsp;//\r\n	extend(Son.prototype,proto);&nbsp;//添加子类特有的原型方法\r\n	extend(Son,Parent);&nbsp;//继承父级的类成员\r\n	&nbsp;\r\n	return&nbsp;Son;\r\n}\r\n\r\nfunction&nbsp;foo(){	}\r\nfoo.prototype={\r\n	aa:11\r\n};\r\n\r\nfunction&nbsp;bar(){	}\r\nbar.prototype={//继承之前原型上有东西是个麻烦\r\n	bb:22\r\n};\r\n\r\nvar&nbsp;bar=inherit(bar,foo,{\r\n	//bb:22,\r\n	cc:function(){\r\n		return&nbsp;&quot;33&quot;;\r\n	},\r\n	dd:44\r\n});\r\n&nbsp;\r\nvar&nbsp;b=new&nbsp;bar;\r\n	console.log(b.aa);\r\n	console.log(b.bb);&nbsp;//之前的bb丢失了\r\n	console.log(b.cc());\r\n	console.log(b.dd);</pre><p><br/></p>',1460086373,0,267,'',''),(8,8,1,'{6}',0,0,'SQL入门',0,0,'MySQL新手入门','<p>基本的数据库操作，增删改查。</p>','<p>基本的数据库操作，增删改查。</p><pre class=\"prism-highlight prism-language-sql\">#&nbsp;插入数据&nbsp;\r\nINSERT&nbsp;INTO&nbsp;user()&nbsp;VALUES(&#39;张三&#39;,&nbsp;&#39;男&#39;);\r\nINSERT&nbsp;INTO&nbsp;user(name,&nbsp;signup_date)&nbsp;VALUES(&#39;张三&#39;,&nbsp;&#39;男&#39;);\r\n\r\n#&nbsp;删除记录&nbsp;\r\nDELETE&nbsp;FROM&nbsp;user&nbsp;WHERE&nbsp;name&nbsp;=&nbsp;&#39;李四&#39;&nbsp;;\r\nDELETE&nbsp;FROM&nbsp;user&nbsp;WHERE&nbsp;id&nbsp;&lt;&nbsp;123;\r\n\r\n#&nbsp;更新数据&nbsp;\r\nUPDATE&nbsp;user&nbsp;set&nbsp;name&nbsp;=&nbsp;&#39;李四&#39;&nbsp;WHERE&nbsp;name&nbsp;=&nbsp;&#39;张三&#39;;\r\nUPDATE&nbsp;user&nbsp;set&nbsp;name&nbsp;=&nbsp;&#39;赵六&#39;&nbsp;WHERE&nbsp;id&nbsp;=&nbsp;123;\r\n\r\n#&nbsp;选择记录&nbsp;\r\nSELECT&nbsp;*&nbsp;FROM&nbsp;user;\r\nSELECT&nbsp;id,name&nbsp;FROM&nbsp;user;</pre><p>sql子句顺序：</p><pre class=\"prism-highlight prism-language-sql\">where，group&nbsp;By，order&nbsp;By，limit\r\n#严格顺序，否则报错。</pre><p><br/></p>',1460425589,0,41,'',''),(9,5,1,'',1,0,'',0,0,'深入理解DOM事件流的三个阶段','<p>深入理解DOM事件流的三个阶段</p>','<p>深入理解DOM事件流的三个阶段</p>',1460654389,0,2,'',''),(37,6,1,'{10}',0,0,'',0,0,'布局神器display:table-cell','<p>　　在需要兼容ie6、ie7的时代我也尽量避免使用float来布局，左右布局能用display:inline-block;布局我就用它来布局，但是还是无法完全不使用它，很多布局例如需要靠左和靠右的布局场景下就没办法不去使用float来布局。</p>','<p style=\"text-align: center;\"><img src=\"{#ZC_BLOG_HOST#}zb_users/upload/2016/06/201606061465198898549242.jpeg\" alt=\"table-cell.jpeg\"/></p><p>　　随着时间的推进，ie6、ie7在中国浏览器市场的占有率越来越低的情况，我现在工作的团队，经过用户访问数据，得知ie6、ie7的用户已经非常少的前提下，决定不在兼容ie6、ie7。正式在这个激动人心的决定后，让我对display:table-cell；这个属性有了更加深入的应用和理解。在ie8还必须兼容的pc端，它绝对是一个现代的布局神器。</p><p>　　</p><p>　　我并不喜欢用float来做布局，因为它触发的问题比较多，例如要清除浮动，元素浮动后还会导致该元素脱离文档流，即使你清除float，该元素依旧是脱离文档流。</p><p>　　</p><p>　　在需要兼容ie6、ie7的时代我也尽量避免使用float来布局，左右布局能用display:inline-block;布局我就用它来布局，但是还是无法完全不使用它，很多布局例如需要靠左和靠右的布局场景下就没办法不去使用float来布局。我现在切页面很少会去在ie下查看效果，大部分是通过chrome来进行调试的，等整个页面切好了，再用ie过一遍页面，大部分时候页面是没什么太多兼容问题，这或许多年来工作累积的经验使我写代码时避开了一些兼容坑，也可能是自己对盒模型摸索许久以来的一些经验。</p><p>　　</p><p>　　废话不多说，下面直接看demo例子，好可以快速爱上display:inline-block;</p><p>　　</p><p>　　在需要兼容ie6、ie7的时代我也尽量避免使用float来布局，左右布局能用display:inline-block;布局我就用它来布局，但是还是无法完全不使用它，很多布局例如需要靠左和靠右的布局场景下就没办法不去使用float来布局。</p><p>　　</p><p>　　移动端布局因为有display:box这等属性，所以table-cell相对就排不上什么大用场，不过在移动端你要用table-cell也不是不可以，根据自己对属性的理解去使用就可以了。</p>',1465198435,0,594,'',''),(19,1,1,'',0,0,'',0,0,'Linux解压缩命令全览','<p>此文收集整理了一些Linux系统常见的解压缩命令</p>','<p><strong style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size:12px\"><span style=\"color:#0000ff\">.tar</span></span></strong><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: #333333; font-family: Arial; line-height: 26px; background-color: #FFFFFF;\">解包：tar xvf FileName.tar</span><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: #333333; font-family: Arial; line-height: 26px; background-color: #FFFFFF;\">打包：tar cvf FileName.tar DirName</span><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color:#008000;font-family: Arial; line-height: 26px; white-space: normal; background-color: #FFFFFF;\">（注：tar是打包，不是压缩！）</span><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: #333333; font-family: Arial; line-height: 26px; background-color: #FFFFFF;\">---------------------------------------------</span><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><strong style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size:12px\"><span style=\"color:#0000ff\">.gz</span></span></strong><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: #333333; font-family: Arial; line-height: 26px; background-color: #FFFFFF;\">解压1：gunzip FileName.gz</span><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: #333333; font-family: Arial; line-height: 26px; background-color: #FFFFFF;\">解压2：gzip -d FileName.gz</span><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: #333333; font-family: Arial; line-height: 26px; background-color: #FFFFFF;\">压缩：gzip FileName</span><br style=\"color: rgb(51, 51, 51); font-family: Arial; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);\"/><s